import { BookCreationInput } from "@/graphql/generated/inputs/book.inputs";
import { ID } from "@/graphql/schema/types";
import { Payload } from "@/utils/data";

export type BookData = BookCreationInput & { id?: ID };
// export type BookData = InputData<Book>;
// export type InitialBookData = InitialData<Book, "rank" | "userId">;
export type PartialBookData = Partial<BookData>;

export function initializeBookData(data: PartialBookData): BookData {
  return {
    title: "",
    slug: "",
    authorNames: [],
    authorNamesLf: [],
    ...data,
  };
}

export function bookDataReducer(state: BookData, payload: Payload<BookData>) {
  if (payload.field === "init") return initializeBookData(payload.value as PartialBookData);
  return { ...state, [payload.field]: payload.value };
}
