import { NoteFragment } from "@/graphql/generated/fragments/note.fragment";
import { UserFragment } from "@/graphql/generated/fragments/user.fragment";
import { NoteCreationInput } from "@/graphql/generated/inputs/note.inputs";
import { ID } from "@/graphql/schema/types";
import { ArrayAction, arrayReducer, Payload } from "@/utils/data/reduction";

export type NoteData = Partial<NoteCreationInput> & { id?: ID };
// export type NoteData = InputData<Note>;
// export type InitialNoteData = InitialData<Note, "rank" | "userId">;

export function initializeNoteData(
  data: Partial<NoteData>,
  user?: UserFragment | null | undefined
): Partial<NoteData> {
  if (!user) return data;
  return {
    userId: user.id,
    title: "",
    ...Object.fromEntries(Object.entries(data).filter(([, value]) => value !== undefined)), // TODO: make this unnecessary
  };
}

export function noteReducer(state: NoteData, payload: Payload<NoteData>) {
  if (payload.field === "init") return initializeNoteData(payload.value as Partial<NoteData>);
  return { ...state, [payload.field]: payload.value };
}

export function notesReducer(state: NoteFragment[], action: ArrayAction<NoteFragment>) {
  return arrayReducer<NoteFragment>(state, action);
}
